    /**
     * Erzeugt eine der 14 Standard Schriftarten mit WinAnsiEncoding
     * @param PdfFile $pdfFile PdfFile, in welchem die Schriftart eingebettet werden soll
     * @return FontType1
     * @throws \Exception Kann eigentlich nicht passieren
     */
    public static function Courier(PdfFile $pdfFile) : FontType1 {
        return new FontType1(new PdfDictionary([
            "Type" => new PdfName("Font"),
            "Subtype" => new PdfName("Type1"),
            "BaseFont" => new PdfName("Courier"),
            "Encoding" => new PdfName("WinAnsiEncoding")
        ]), $pdfFile);
    }

